mapper.select:
SELECT id, title, message, update_time AS updateTime, user_email AS userEmail, name, paper_name AS paperName
FROM message
WHERE user_email = #{userEmail};

SELECT id, title, message, update_time AS updateTime, user_email AS userEmail, name, paper_name AS paperName
FROM message
WHERE paper_name = #{paperName};

mapper.update:
UPDATE message
title = #{title}, message = #{message}, update_time = #{updateTime}, user_email = #{userEmail}, name = #{name}, paper_name = #{paperName}
WHERE paper_name = #{paperName};
